mapper.select:
select date_format(tmp.cur_time, '%Y-%m-%d %H:%i') as time, count(*) as count from (select sri.cur_time, (sri.cur_price - sri.pre_close_price) / sri.pre_close_price as increase from stock_rt_info as sri where sri.cur_time between #{startTime} and #{endTime}
having increase >= 0.1
having increase <= -0.1
) as tmp group by tmp.cur_time order by tmp.cur_time asc;
